mapper.select:
select u.username, r.role_name, r.role_id
from user as u
         inner join role as r
                    on u.role_id = r.role_id
where u.account = #{account}
  and u.password = #{password};

select role_id
from user
where account = #{account};

select role_purview
from role
where role_id = #{roleId};
